const body = $json.body || $json;
const message = body.message || '';
const language = body.language || 'en';
const history = body.history || [];
const latitude = body.latitude;
const longitude = body.longitude;
const location = body.location || null;
const mcpServers = body.mcpServers || { global: [], regional: [], allTools: [] };
const detectedRegions = body.detectedRegions || [];
const mcpResults = body.mcpResults || {};
const intentsDetected = body.intentsDetected || [];

const languageMap = {
  'hi': 'Hindi', 'te': 'Telugu', 'kn': 'Kannada', 'mr': 'Marathi',
  'gu': 'Gujarati', 'ta': 'Tamil', 'bn': 'Bengali', 'ml': 'Malayalam',
  'pa': 'Punjabi', 'or': 'Odia', 'sw': 'Swahili', 'am': 'Amharic',
  'vi': 'Vietnamese', 'th': 'Thai', 'id': 'Indonesian', 'en': 'English'
};
const langName = languageMap[language] || language;

let locationContext = '';
if (location) {
  const parts = [location.locality, location.city, location.district, location.state, location.country].filter(Boolean);
  locationContext = parts.join(', ') || location.displayName || '';
}

return {
  json: {
    message, language, langName, latitude, longitude, locationContext,
    history, mcpServers, detectedRegions, location, mcpResults, intentsDetected
  }
};

// Pass MCP data from API Gateway (already called MCP servers)
// Just pass through the data received
return { json: $json };

const langName = $json.langName;
const locationContext = $json.locationContext;
const history = $json.history || [];
const message = $json.message;
const mcpResults = $json.mcpResults || {};
const detectedRegions = $json.detectedRegions || [];
const intentsDetected = $json.intentsDetected || [];

let mcpContext = '';

// Weather data with interpretation
if (mcpResults.weather?.current) {
  const w = mcpResults.weather.current;
  let tempNote = w.temperature < 15 ? 'cool weather' : w.temperature > 32 ? 'hot, watch for heat stress' : 'comfortable for field work';
  let humidNote = w.humidity > 80 ? 'high humidity, watch for fungal diseases' : w.humidity < 40 ? 'dry air, crops may need water' : 'moderate humidity';
  let windNote = w.wind_speed > 25 ? 'too windy for spraying' : 'safe for spraying if needed';
  mcpContext += `\n\n**WEATHER DATA:**\n- Temperature: ${w.temperature}Â°C (${tempNote})\n- Humidity: ${w.humidity}% (${humidNote})\n- Wind: ${w.wind_speed} km/h from ${w.wind_direction} (${windNote})\n- Conditions: ${w.conditions}\n- Precipitation: ${w.has_precipitation ? 'Rain expected' : 'No rain expected'}`;
}

// Soil data with interpretation
if (mcpResults.soil?.properties) {
  const p = mcpResults.soil.properties;
  const ph = p.ph?.[0]?.value?.value;
  const n = p.nitrogen_total?.[0]?.value?.value;
  const phos = p.phosphorous_extractable?.[0]?.value?.value;
  const k = p.potassium_extractable?.[0]?.value?.value;
  const texture = p.texture_class?.[0]?.value?.value;

  let phNote = ph < 5.5 ? 'too acidic, add lime' : ph > 7.5 ? 'alkaline, may need sulfur' : 'good for most crops';
  let nNote = n < 1.0 ? 'low, add urea or compost' : n > 1.5 ? 'good nitrogen level' : 'medium, light fertilizer recommended';
  let phosNote = phos < 10 ? 'low, add DAP or bone meal' : phos > 20 ? 'excellent' : 'adequate for most crops';
  let kNote = k < 150 ? 'low, add potash' : k > 250 ? 'excellent for fruiting' : 'adequate';

  mcpContext += `\n\n**SOIL DATA:**\n- pH: ${ph} (${phNote})\n- Nitrogen: ${n} g/kg (${nNote})\n- Phosphorus: ${phos} ppm (${phosNote})\n- Potassium: ${k} ppm (${kNote})\n- Soil Type: ${texture || 'Unknown'}`;
}

// Fertilizer data - SSFR Format per Google Doc requirements
if (mcpResults.fertilizer?.fertilizers) {
  const f = mcpResults.fertilizer;
  const crop = f.crop || 'crop';
  const loc = locationContext || 'your farm';

  mcpContext += `\n\n**FERTILIZER RECOMMENDATION for ${crop.toUpperCase()} at ${loc}:**`;

  // Organic fertilizers section
  if (f.fertilizers.organic) {
    mcpContext += `\n\n1. Organic Fertilizers:`;
    if (f.fertilizers.organic.compost) {
      mcpContext += `\n   - Compost: ${f.fertilizers.organic.compost} tons/ha (spread before plowing, improves soil health)`;
    }
    if (f.fertilizers.organic.vermicompost) {
      mcpContext += `\n   - Vermicompost: ${f.fertilizers.organic.vermicompost} tons/ha (worm compost, very nutrient-rich)`;
    }
  }

  // Inorganic fertilizers section
  if (f.fertilizers.inorganic) {
    mcpContext += `\n\n2. Inorganic Fertilizers:`;
    if (f.fertilizers.inorganic.nps) {
      mcpContext += `\n   - NPS: ${f.fertilizers.inorganic.nps} kg/ha (apply at planting - provides Nitrogen, Phosphorus, Sulfur)`;
    }
    if (f.fertilizers.inorganic.urea) {
      mcpContext += `\n   - UREA: ${f.fertilizers.inorganic.urea} kg/ha (apply 30-45 days after planting - provides Nitrogen for leaf growth)`;
    }
  }

  // Expected yield
  if (f.expected_yield) {
    mcpContext += `\n\n3. Expected Yield: ${f.expected_yield.value} ${f.expected_yield.unit} (if recommendations followed)`;
  }
}

// Feed data with interpretation
if (mcpResults.feed?.feeds?.feeds?.length > 0) {
  const feeds = mcpResults.feed.feeds.feeds.slice(0, 5);
  mcpContext += `\n\n**FEED DATABASE (Ethiopia):**`;
  feeds.forEach(fd => {
    let proteinNote = fd.fd_cp > 30 ? 'very high protein, excellent supplement' : fd.fd_cp > 18 ? 'high protein, good for milk production' : fd.fd_cp > 12 ? 'moderate protein' : 'low protein, use as filler';
    let typeNote = fd.fd_type === 'Concentrate' ? 'energy-rich, give in small amounts' : fd.fd_type === 'Forage' ? 'bulk feed for fiber and stomach fill' : fd.fd_type;
    mcpContext += `\n- ${fd.fd_name}: Protein ${fd.fd_cp?.toFixed(1) || '?'}% (${proteinNote}), DM ${fd.fd_dm?.toFixed(1) || '?'}%, Type: ${typeNote}`;
  });
}

// Plant Diagnosis data from AgriVision MCP
if (mcpResults.diagnosis?.crop) {
  const d = mcpResults.diagnosis;
  mcpContext += `\n\n**PLANT HEALTH DIAGNOSIS (AI Analysis):**`;
  mcpContext += `\n- Crop Identified: ${d.crop.name}${d.crop.scientific_name ? ' (' + d.crop.scientific_name + ')' : ''} - Confidence: ${d.crop.confidence}`;
  mcpContext += `\n- Health Status: ${d.health_status} (${d.health_confidence} confidence)`;
  if (d.growth_stage) mcpContext += `\n- Growth Stage: ${d.growth_stage}`;

  if (d.issues && d.issues.length > 0) {
    mcpContext += `\n\n**Issues Detected:**`;
    d.issues.forEach((issue, i) => {
      mcpContext += `\n\n${i + 1}. ${issue.name}${issue.scientific_name ? ' (' + issue.scientific_name + ')' : ''}`;
      mcpContext += `\n   - Category: ${issue.category}`;
      mcpContext += `\n   - Severity: ${issue.severity}${issue.stage ? ' (' + issue.stage + ' stage)' : ''}`;
      mcpContext += `\n   - Affected Parts: ${(issue.affected_parts || []).join(', ') || 'Unknown'}`;
      mcpContext += `\n   - Symptoms: ${(issue.symptoms || []).join(', ') || 'See image'}`;
      if (issue.causal_agent) mcpContext += `\n   - Cause: ${issue.causal_agent}`;
    });
  } else {
    mcpContext += `\n\nâœ… No diseases or pests detected. Plant appears healthy!`;
  }

  if (d.diagnostic_notes) mcpContext += `\n\n**Notes:** ${d.diagnostic_notes}`;
  if (d.requires_lab_test) mcpContext += `\n\nâš ï¸ Lab testing recommended for confirmation.`;
}

const systemPrompt = `You are a friendly farming advisor helping smallholder farmers. RESPOND ONLY IN ${langName}.${locationContext ? `\nFarmer location: ${locationContext}` : ''}${detectedRegions.length > 0 ? `\nRegion: ${detectedRegions.map(r => r.name).join(', ')}` : ''}${mcpContext ? `\n\n## REAL-TIME DATA FROM AGRICULTURAL SERVICES:${mcpContext}` : ''}

## HOW TO PRESENT DATA TO FARMERS (CRITICAL)

1. **Always show the number AND a simple explanation:**
   - GOOD: "pH is 6.5 - slightly acidic to neutral, ideal for maize and wheat"
   - BAD: "pH is 6.5" (no explanation)

2. **For FERTILIZER recommendations, use this EXACT format:**
   - Title: "Fertilizer Recommendation for [CROP] at [LOCATION]"
   - Section 1: Organic Fertilizers (compost, vermicompost)
   - Section 2: Inorganic Fertilizers (NPS, UREA)
   - Section 3: Expected Yield
   - Explain WHEN to apply each fertilizer (at planting vs top dressing)
   - Explain WHAT each fertilizer does (NPS = starter, UREA = growth)

3. **Translate technical terms to farmer language:**
   - "NPS" â†’ "NPS fertilizer (Nitrogen-Phosphorus-Sulfur blend)"
   - "UREA" â†’ "Urea (high-nitrogen fertilizer for leaf growth)"
   - "tons/ha" â†’ "tons per hectare"

4. **Add actionable farming advice:**
   - "Apply NPS at planting time in the furrow"
   - "Apply Urea as top-dressing when plants are 30-45 days old"
   - "Split Urea application into 2 doses if possible"

5. **Use local examples and context:**
   - Ethiopia: Reference teff, maize, wheat, noug
   - Use Ethiopian units and local fertilizer brands if known

6. **When translating to local languages:**
   - Keep numbers as-is (198.92 kg/ha stays "198.92 kg/ha")
   - Translate the explanations fully
   - Use local names for fertilizers when known (e.g., áˆ›á‹³á‰ áˆªá‹« for fertilizer in Amharic)

## RESPONSE FORMAT
1. Greeting with emoji relevant to topic (ðŸŒ¤ï¸ weather, ðŸŒ± soil, ðŸ„ feed, ðŸŒ¾ fertilizer)
2. Clear title for the recommendation
3. Numbered sections with bullet points
4. **Bold numbers** with simple explanations in parentheses
5. End with encouragement and next step suggestion

## RESPONSE LENGTH GUIDELINES (Context-Aware)
- Simple factual questions: 2-4 sentences
- How-to questions: Numbered steps (3-6 typical)
- Diagnosis questions: Complete with problem, cause, treatment, prevention sections
- Complex questions with data: Detailed sections with all relevant information

IMPORTANT: Always complete your response fully. Never stop mid-sentence or mid-section.

---FOLLOWUP---
- Question 1
- Question 2
- Question 3`;

const contents = [
  { role: 'user', parts: [{ text: systemPrompt }] },
  { role: 'model', parts: [{ text: `I understand! I'll format fertilizer recommendations with clear sections (Organic, Inorganic), include application timing, and explain what each fertilizer does - all in ${langName}.` }] }
];

for (const msg of history.slice(-10)) {
  contents.push({ role: msg.isBot ? 'model' : 'user', parts: [{ text: msg.text }] });
}
contents.push({ role: 'user', parts: [{ text: message }] });

return { json: { ...$json, requestBody: { contents, generationConfig: { temperature: 0.7, maxOutputTokens: 2048 } } } };

const response = $json;
const prevData = $('Build Gemini Prompt').item.json;

try {
  const fullText = response.candidates?.[0]?.content?.parts?.[0]?.text || '';
  let mainAnswer = fullText;
  let followUpQuestions = [];

  const markerIndex = fullText.indexOf('---FOLLOWUP---');
  if (markerIndex !== -1) {
    mainAnswer = fullText.substring(0, markerIndex).trim();
    const followupSection = fullText.substring(markerIndex + 14).trim();
    followUpQuestions = followupSection.split('\n').filter(l => l.trim().startsWith('- ')).map(l => l.replace(/^-\s*/, '').trim()).slice(0, 3);
  }

  const mcpServers = prevData.mcpServers || {};
  const globalCount = mcpServers.global?.length || 0;
  const regionalCount = mcpServers.regional?.length || 0;

  return {
    json: {
      success: true,
      response: mainAnswer,
      followUpQuestions,
      language: prevData.language,
      location: prevData.locationContext,
      regions: prevData.detectedRegions?.map(r => r.name) || [],
      mcpContext: { globalServices: globalCount, regionalServices: regionalCount, totalServices: globalCount + regionalCount },
      mcpToolsUsed: Object.keys(prevData.mcpResults || {}),
      intentsDetected: prevData.intentsDetected || []
    }
  };
} catch (e) {
  return { json: { success: false, response: 'An error occurred.', error: e.message } };
}

