// Prepare Cloudinary image upload
const crypto = require('crypto');

const imageBase64 = $json.body?.image || '';
const mimeType = $json.body?.mimeType || 'image/jpeg';

const timestamp = Math.floor(Date.now() / 1000);
const publicId = `plants/image_${timestamp}`;
const apiSecret = $env.CLOUDINARY_API_SECRET;

const paramsToSign = `public_id=${publicId}&timestamp=${timestamp}`;
const signature = crypto
  .createHash('sha1')
  .update(paramsToSign + apiSecret)
  .digest('hex');

return {
  json: {
    timestamp,
    publicId,
    signature,
    apiKey: $env.CLOUDINARY_API_KEY,
    cloudName: $env.CLOUDINARY_CLOUD_NAME,
    dataUri: `data:${mimeType};base64,${imageBase64}`
  }
};

// Format response with Cloudinary URLs
const response = $json;

return {
  json: {
    success: true,
    imageUrl: response.secure_url,
    publicId: response.public_id,
    width: response.width,
    height: response.height,
    format: response.format,
    bytes: response.bytes,
    // Transformed URLs for different sizes
    thumbnailUrl: response.secure_url.replace('/upload/', '/upload/w_200,h_200,c_fill/'),
    mediumUrl: response.secure_url.replace('/upload/', '/upload/w_800,c_scale/')
  }
};
