// Prepare prompt for title generation
const { messages, language } = $input.first().json.body;

// Take first 10 messages for context
const contextMessages = (messages || []).slice(0, 10);

const conversationSummary = contextMessages
  .map(m => `${m.role}: ${(m.content || '').slice(0, 200)}`)
  .join('\n');

const languageInstruction = language && language !== 'en' 
  ? `Write the title in ${language} language` 
  : 'Write in English';

const prompt = `Generate a short, descriptive title (maximum 50 characters) for this farming assistant conversation. The title should capture the main topic or question.

Conversation:
${conversationSummary}

Rules:
- Maximum 50 characters
- Be specific (e.g., "Maize leaf disease help" not "Plant problem")
- ${languageInstruction}
- No quotes or special characters
- Capitalize first letter of each major word

Return ONLY the title, nothing else.`;

return {
  prompt,
  messages: contextMessages,
  language: language || 'en',
  hasSufficientContent: contextMessages.length >= 1
};

// Extract and clean the title from Gemini response
const geminiResponse = $input.first().json;

let title = geminiResponse?.candidates?.[0]?.content?.parts?.[0]?.text?.trim() || '';

// Clean up the title
title = title
  .replace(/^["']|["']$/g, '') // Remove quotes
  .replace(/\n/g, ' ')          // Remove newlines
  .trim();

// Truncate if too long
if (title.length > 50) {
  const lastSpace = title.slice(0, 50).lastIndexOf(' ');
  if (lastSpace > 30) {
    title = title.slice(0, lastSpace) + '...';
  } else {
    title = title.slice(0, 47) + '...';
  }
}

// Fallback if empty
if (!title) {
  title = 'New Conversation';
}

return {
  success: true,
  title,
  generatedAt: new Date().toISOString()
};

// Fallback title when no messages
return {
  success: true,
  title: 'New Conversation',
  generatedAt: new Date().toISOString(),
  fallback: true
};
