const audioBase64 = $json.body?.audio || "";
const language = $json.body?.language || "en";
const mimeType = $json.body?.mimeType || "audio/wav";

const languageMap = {
  "hi": "Hindi", "te": "Telugu", "kn": "Kannada",
  "mr": "Marathi", "gu": "Gujarati", "ta": "Tamil",
  "bn": "Bengali", "ml": "Malayalam", "pa": "Punjabi",
  "or": "Odia", "sw": "Swahili", "am": "Amharic",
  "vi": "Vietnamese", "th": "Thai", "id": "Indonesian",
  "en": "English", "es": "Spanish", "fr": "French",
  "de": "German", "pt": "Portuguese", "ar": "Arabic"
};

const langName = languageMap[language] || language;
const prompt = `Transcribe this audio accurately. The speaker is likely using ${langName}. Return ONLY the transcribed text, no explanations.`;

return {
  json: {
    audioBase64, language, mimeType, prompt,
    requestBody: {
      contents: [{
        parts: [
          { text: prompt },
          { inlineData: { mimeType: mimeType, data: audioBase64 } }
        ]
      }]
    }
  }
};

const response = $json;
try {
  const text = response.candidates?.[0]?.content?.parts?.[0]?.text || "";
  const language = $("Prepare Transcription").item.json.language;
  return {
    json: {
      success: true,
      text: text.trim(),
      language: language
    }
  };
} catch (e) {
  return { json: { success: false, error: e.message, text: "" } };
}
